import React, { useEffect, useState } from "react";

function App() {
  const [count, setCount] = useState(0);
  const increment = () => {
    setCount(count + 1);
  };
  const decrement = () => {
    setCount(count - 1);
  };
  const reset = () => {
    setCount(0);
  };

  return (
    <div className="flex justify-center items-center h-20">
      <button className="bg-red-200" onClick={increment}>
        counter+
      </button>
      <button className="bg-red-900" onClick={decrement}>
        counter-
      </button>
      <button onClick={reset}>reset</button>
      {count}
    </div>
  );
}
export default App;
